                        i.putExtra(Intent.EXTRA_TEXT, "body of email")
                        try {
                            startActivity(Intent.createChooser(i, "Send mail..."))
                        } catch (ex: ActivityNotFoundException) {
                            Toast.makeText(
                                this,
                                "There are no email clients installed.",
                                Toast.LENGTH_SHORT
                            ).show()
                        }
                    }

                    fabsuggestPapers.setOnClickListener {
                        val url = "https://api.whatsapp.com/send?phone=+918088208378"
                        val i = Intent(Intent.ACTION_VIEW)
                        i.data = Uri.parse(url)
                        startActivity(i)
                    }
                    mShare.setOnClickListener {
                        val msg = " This App which provide news papers of 7 " +
                                "languages,there websites and live channels also (130+ news papers in one App) <link> "

                        val sendIntent = Intent()
                        sendIntent.action = Intent.ACTION_SEND
                        sendIntent.putExtra(Intent.EXTRA_TEXT, msg)
                        sendIntent.type = "text/plain"
                        startActivity(sendIntent)
                    }

                }


