                private fun changevisible(
                    x: TextView?,
                    y: RecyclerView?,
                    j: Int
                ) {
                    pripAd()

                    for (i in 0..7) {
                        refrence[i] = 0
                    }
                    refrence[j] = 1



                    rKannada.visibility = View.GONE
                    rEnglish.visibility = View.GONE
                    rHindi.visibility = View.GONE
                    rMalayalam.visibility = View.GONE
                    rMarathi.visibility = View.GONE
                    rTamil.visibility = View.GONE
                    rTelagu.visibility = View.GONE
                    rInternational.visibility = View.GONE


                    Kannada.setTextColor(resources.getColorStateList(R.color.White))
                    Hindi.setTextColor(resources.getColorStateList(R.color.White))
                    Malayalam.setTextColor(resources.getColorStateList(R.color.White))
                    Marathi.setTextColor(resources.getColorStateList(R.color.White))
                    Tamil.setTextColor(resources.getColorStateList(R.color.White))
                    Telagu.setTextColor(resources.getColorStateList(R.color.White))
                    International.setTextColor(resources.getColorStateList(R.color.White))
                    English.setTextColor(resources.getColorStateList(R.color.White))

                    Kannada.textSize = 24f
                    Hindi.textSize = 24f
                    Malayalam.textSize = 24f
                    Marathi.textSize = 24f
                    Tamil.textSize = 24f
                    Telagu.textSize = 24f
                    International.textSize = 24f
                    English.textSize = 24f

                    x?.setTextColor(resources.getColorStateList(R.color.Selected))

                    x?.textSize = 30f
                    y?.visibility = View.VISIBLE

                    y?.layoutManager = GridLayoutManager(this, 3)
                    y?.adapter = adapter

                }

