                fun pripAd() {
                    MobileAds.initialize(this)
                    mInterstitialAd = InterstitialAd(this)
                    mInterstitialAd.adUnitId = "ca-app-pub-4086734586547743/5587222304"
                    mInterstitialAd.loadAd(AdRequest.Builder().build())
                }

                override fun onBackPressed() {
                    if (rEnglish.visibility == View.VISIBLE) {
                        changevisible(TextView(this), RecyclerView(this), 0)
                        return
                    }
                    if (rHindi.visibility == View.VISIBLE) {
                        changevisible(TextView(this), RecyclerView(this), 0)
                        return
                    }
                    if (rKannada.visibility == View.VISIBLE) {
                        changevisible(TextView(this), RecyclerView(this), 0)
                        return
                    }
                    if (rMarathi.visibility == View.VISIBLE) {
                        changevisible(TextView(this), RecyclerView(this), 0)
                        return
                    }
                    if (rTelagu.visibility == View.VISIBLE) {
                        changevisible(TextView(this), RecyclerView(this), 0)
                        return
                    }
                    if (rTamil.visibility == View.VISIBLE) {
                        changevisible(TextView(this), RecyclerView(this), 0)
                        return
                    }
                    if (rMalayalam.visibility == View.VISIBLE) {
                        changevisible(TextView(this), RecyclerView(this), 0)
                        return
                    }
                    if (rInternational.visibility == View.VISIBLE) {
                        changevisible(TextView(this), RecyclerView(this), 0)
                        return
                    }
                    super.onBackPressed()
                    finish()
                }
